DROP TABLE IF EXISTS dim_country;

CREATE TABLE IF NOT EXISTS dim_country(
    country_code STRING,
    country_name STRING
);

INSERT INTO dim_country VALUES
('DNK', 'Denmark'),
('SWE', 'Sweden'),
('NOR', 'Norway');

SELECT * FROM dim_country;

DROP TABLE IF EXISTS dim_year;

CREATE TABLE dim_year AS
SELECT explode(sequence(1980, 2025)) AS year;

SELECT * FROM dim_year;
